fn get_move(c: char) -> i32 {
    match c {
        '(' =>  1,
        ')' => -1,
        _   =>  0,
    }
}

pub fn main(instructions: &str) -> i32 {
    let mut floor = 0;

    for c in instructions.chars() {
        floor += get_move(c);
    }

    floor
}

pub fn extra(instructions: &str) -> i32 {
    let mut floor = 0;

    for (j, c) in instructions.chars().enumerate() {
        floor += get_move(c);

        match floor {
            -1 => return (j as i32) + 1,
            _  => (),
        }
    }

    -1 // Failure
}

#[cfg(test)]
pub mod tests {
    use super::*;

    struct Expected<'a> {
        input: &'a str,
        result: i32
    }

    #[test]
    fn test_day1a() {

        let expected_answers = [
            Expected { input: "(())",    result:  0 },
            Expected { input: "()()",    result:  0 },
            Expected { input: "(((",     result:  3 },
            Expected { input: "(()(()(", result:  3 },
            Expected { input: "))(((((", result:  3 },
            Expected { input: "())",     result: -1 },
            Expected { input: "))(",     result: -1 },
            Expected { input: ")))",     result: -3 },
            Expected { input: ")())())", result: -3 },
            ];

        for check in &expected_answers {
            match check {
                &Expected { input, result } => {
                    assert_eq!( main(input), result );
                },
            }
        }
    }

    #[test]
    fn test_day1b () {
        let expected_answers = [
            Expected { input: ")", result: 1 },
            Expected { input: "()())", result: 5 },
            ];

        for check in &expected_answers {
            match check {
                &Expected { input, result } => {
                    assert_eq!( extra(input), result );
                },
            }
        }
    }
}

